mapper.select:
select b.id, appreciation, commentabled, content, b.create_time, first_picture, flag, published, recommend, share_statement, title, b.update_time, views, type_id, user_id, description, type.name tname,type.id tid,u.id uid,u.nickname uname,u.avatar uavatar,u.username from blog.t_blog b left join blog.t_type type on b.type_id=type.id left join blog.t_user u on u.id=b.user_id
and b.title like concat('%',#{title},'%')
and b.type_id = #{typeId}
and b.recommend = #{recommend}

select
b.id, appreciation, commentabled, content, b.create_time, first_picture, flag, published, recommend,
share_statement, title, b.update_time, views, type_id, user_id, description,type.name tname,type.id tid,
u.id uid,u.nickname uname,u.avatar uavatar,u.username
from blog.t_blog b
left join blog.t_type type
on b.type_id=type.id
left join blog.t_user u
on u.id=b.user_id


        and b.id = #{id}


        and appreciation = #{appreciation}


        and commentabled = #{commentabled}


        and content = #{content}


        and create_time = #{createTime}


        and first_picture = #{firstPicture}


        and flag = #{flag}


        and published = #{published}


        and recommend = #{recommend}


        and share_statement = #{shareStatement}


        and title = #{title}


        and update_time = #{updateTime}


        and views = #{views}


        and type_id = #{typeId}


        and user_id = #{userId}


        and description = #{description}
